"use strict";

var _merge = require("./merge");

describe('merge()', function () {
  it('performs shallow level merge', function () {
    var target = {
      foo: 'hello world',
      bar: 123,
      baz: function baz() {},
      qux: {
        boz: 321
      }
    };
    var result = (0, _merge.merge)(target, {
      foo: 'overwritten',
      baz: 'also-overwritten'
    });
    expect(result).toEqual({
      foo: 'overwritten',
      bar: 123,
      baz: 'also-overwritten',
      qux: {
        boz: 321
      }
    });
  });
  it('performs deep level merge', function () {
    var target = {
      foo: 'hello world',
      bar: 123,
      qux: {
        boz: 321
      }
    };
    var result = (0, _merge.merge)(target, {
      qux: {
        boz: 'new-boz',
        baz: 'baz'
      }
    });
    expect(result).toEqual({
      foo: 'hello world',
      bar: 123,
      qux: {
        boz: 'new-boz',
        baz: 'baz'
      }
    });
  });
});